<?php

namespace NotificationChannels\FacebookPoster\Attachments;

class Link
{
    /** @var string */
    protected $url;

    /**
     * @param string $url
     */
    public function __construct($url)
    {
        $this->url = $url;
    }

    /**
     * @return string
     */
    public function getUrl()
    {
        return $this->url;
    }
}
